mapper.update:
update member_user
set is_delete = 1
where id = #{id,jdbcType=BIGINT}

mapper.select:
select t1.*
from member_user t1 ,(
select id from member_user
WHERE is_delete=0

  and member_name = #{memberName,jdbcType=VARCHAR}


  and phone_number = #{phoneNumber,jdbcType=VARCHAR}


  and member_card_number = #{memberCardNumber,jdbcType=VARCHAR}


  and gmt_create >= #{createTimeStart}


  and gmt_create <= #{createTimeEnd}

order by gmt_modified desc
limit #{startRow,jdbcType=INTEGER} ,
#{pageSize,jdbcType=INTEGER}
)t2
WHERE t1.id=t2.id

select count(1) as count
from sys_user WHERE is_delete=0

  and member_name = #{memberName,jdbcType=VARCHAR}


  and phone_number = #{phoneNumber,jdbcType=VARCHAR}


  and member_card_number = #{memberCardNumber,jdbcType=VARCHAR}


  and gmt_create >= #{createTimeStart}


  and gmt_create <= #{createTimeEnd}
